SPDataList.c:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_GIVINGFORECASTLIST
as set nocount on;
SELECT GL.ID , GL.YEARID , GL.DESCRIPTION , case when REVFORCAST.GLFISCALYEARID is null then 'No forecast data found' else 'Forecast already saved' end as FORECASTDATA , case when REVFORCAST.GLFISCALYEARID is null then 0 else 1 end as NEEDFORECAST FROM GLFISCALYEAR GL LEFT JOIN ( SELECT DISTINCT GLFISCALYEARID FROM USR_REVENUECATEGORYBYAPPEALCODEFORECAST ) REVFORCAST ON GL.ID = REVFORCAST.GLFISCALYEARID
