create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_REVENUEBYAPPEALCATEGORYCODEFORCAST ( @ID uniqueidentifier, @DATALOADED bit = 0 output, @TSLONG bigint = 0 output, @FORECASTSTRING nvarchar(max) = null output, @FORECASTDETAILS xml = null output ) as
set nocount on;
-- be sure to set these, in case the select returns no rows set @DATALOADED = 0 set @TSLONG = 0
-- populate the output parameters, which correspond to fields on the form. Note that -- we set @DATALOADED = 1 to indicate that the load was successful. Otherwise, the system -- will display a "no data loaded" message. Also note that we fetch the TSLONG so that concurrency -- can be considered. select TOP 1 @DATALOADED = 1, @TSLONG = TSLONG, @FORECASTSTRING = 'abel' from dbo.USR_REVENUECATEGORYBYAPPEALCODEFORECAST where GLFISCALYEARID = @ID
IF OBJECT_ID('tempdb..#FORECASTLIST') IS NOT NULL BEGIN DROP TABLE #FORECASTLIST END SELECT * INTO #FORECASTLIST FROM ( SELECT RF.APPEALCATEGORYCODEID ,ACC.DESCRIPTION as APPEALCATEGORYCODEDISPLAY ,case RF.MONTHID when 1 then 'MONTHJAN' when 2 then 'MONTHFEB' when 3 then 'MONTHMAR' when 4 then 'MONTHAPR' when 5 then 'MONTHMAY' when 6 then 'MONTHJUN' when 7 then 'MONTHJUL' when 8 then 'MONTHAUG' when 9 then 'MONTHSEP' when 10 then 'MONTHOCT' when 11 then 'MONTHNOV' when 12 then 'MONTHDEC' else 'MONTHUNKNOWN' end as MONTHNM ,RF.EXPECTEDTOTALGIFTAMOUNT as EXPECTEDTOTALGIFTAMOUNT FROM dbo.USR_REVENUECATEGORYBYAPPEALCODEFORECAST RF INNER JOIN dbo.GLFISCALYEAR FY ON RF.GLFISCALYEARID = FY.ID INNER JOIN APPEALCATEGORYCODE ACC ON RF.APPEALCATEGORYCODEID = ACC.ID WHERE FY.ID = @ID
) as SRC PIVOT ( SUM(EXPECTEDTOTALGIFTAMOUNT) FOR MONTHNM IN ([MONTHJAN], [MONTHFEB], [MONTHMAR], [MONTHAPR], [MONTHMAY], [MONTHJUN], [MONTHJUL], [MONTHAUG], [MONTHSEP], [MONTHOCT], [MONTHNOV], [MONTHDEC]) ) as DILI SELECT @FORECASTDETAILS = (SELECT * FROM #FORECASTLIST ORDER BY APPEALCATEGORYCODEDISPLAY for xml raw('ITEM'),type,elements,root('FORECASTDETAILS'),BINARY BASE64)
return 0;
